ELEMENT.HTML:
// display a list of up to 6 recently searched words.

var keywordlist = loadURP("CART::SEARCH_KEYWORDLIST");


if (keywordlist != "") {

	var keywords = keywordlist.split("|");
//For whatever reason, keywordlist is not blank, even when blank.  So the search header gets put into another if statement.  look into this.
	if(keywords.length > 0)	{
		write("<div style='font-weight:bold; font-size:8pt;'>Recent Searches:</div>");
		}

// a max of six results should be displayed, so we set max to 6 if there are more words than that in the keywords array.
	var max = keywords.length;
	if(keywords.length > 6)
		max = 6;
	for (var i=0; i < max; i++) {
		write("<div  class='searchKeywords'><a href='javascript:loadResults(\""+keywords[i]+"\");'>"+keywords[i]+"</a></div>\n");
		}
	}
